import React from 'react';
import ImageSearchEngine from './imageSearchEngine/imageSearchEngine';

const API_KEY = "28646522-b2d4f5beb5fa6bf47614c2ce5"
const API_URL = `https://pixabay.com/api/?key=${API_KEY}&q=yellow+flowers&image_type=photo`

class App extends React.Component {

    state = {
        images: []
    }

    handleGetRequest = async (event) => {
        event.preventDefault()
        const searchWord = event.target.searchValue.value
        const request = await fetch(API_URL)
        const response = await request.json()
        this.setState({images: response.hits})
        console.log(this.state.images)
        console.log(searchWord)
    }

    render() {
        return (
        <div>
            <ImageSearchEngine handleGetRequest={this.handleGetRequest}/>
        </div>
        )
    }
}

export default App;
